// src/App.js

import React, { useState, useEffect } from "react";
import { Upload, CheckCircle, Activity } from "lucide-react";

const ProgressBar = ({
  percentage = 0,
  type = "upload", // 'upload', 'processing', 'task'
  label = "",
  backgroundColor = "#e5e7eb",
  fillColor = "#3b82f6",
  showIcon = true,
  size = "medium", // 'small', 'medium', 'large'
}) => {
  const [displayPercentage, setDisplayPercentage] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    if (displayPercentage !== percentage) {
      setIsAnimating(true);
      const timer = setTimeout(() => {
        setDisplayPercentage(percentage);
        setTimeout(() => setIsAnimating(false), 300);
      }, 50);
      return () => clearTimeout(timer);
    }
  }, [percentage, displayPercentage]);

  const getIcon = () => {
    if (!showIcon) return null;

    switch (type) {
      case "upload":
        return <Upload className="progress-icon" />;
      case "processing":
        return (
          <Activity
            className={`progress-icon ${isAnimating ? "spinning" : ""}`}
          />
        );
      case "task":
        return displayPercentage === 100 ? (
          <CheckCircle className="progress-icon completed" />
        ) : (
          <Activity className="progress-icon" />
        );
      default:
        return <Activity className="progress-icon" />;
    }
  };

  const getStatusText = () => {
    if (displayPercentage === 100) {
      switch (type) {
        case "upload":
          return "Upload Complete";
        case "processing":
          return "Processing Complete";
        case "task":
          return "Task Completed";
        default:
          return "Complete";
      }
    }

    switch (type) {
      case "upload":
        return "Uploading...";
      case "processing":
        return "Processing...";
      case "task":
        return "In Progress...";
      default:
        return "Loading...";
    }
  };

  const sizeClasses = {
    small: "progress-container-small",
    medium: "progress-container-medium",
    large: "progress-container-large",
  };

  return (
    <div className={`progress-wrapper ${sizeClasses[size]}`}>
      <div className="progress-header">
        <div className="progress-info">
          {getIcon()}
          <div className="progress-text">
            <div className="progress-label">{label || getStatusText()}</div>
            <div className="progress-status">{displayPercentage}% complete</div>
          </div>
        </div>
        <div
          className={`progress-percentage ${
            displayPercentage === 100 ? "completed" : ""
          }`}
        >
          {displayPercentage}%
        </div>
      </div>

      <div
        className="progress-bar-container"
        style={{ backgroundColor }}
        role="progressbar"
        aria-valuenow={displayPercentage}
        aria-valuemin="0"
        aria-valuemax="100"
        aria-label={`${
          label || getStatusText()
        } - ${displayPercentage}% complete`}
      >
        <div
          className={`progress-bar-fill ${
            displayPercentage === 100 ? "completed" : ""
          }`}
          style={{
            width: `${displayPercentage}%`,
            backgroundColor: displayPercentage === 100 ? "#10b981" : fillColor,
          }}
        >
          <div className="progress-bar-shine"></div>
        </div>

        {displayPercentage > 0 && displayPercentage < 100 && (
          <div className="progress-particles">
            <div className="particle particle-1"></div>
            <div className="particle particle-2"></div>
            <div className="particle particle-3"></div>
          </div>
        )}
      </div>

      {displayPercentage === 100 && (
        <div className="success-message">
          <CheckCircle className="success-icon" />
          <span>Successfully completed!</span>
        </div>
      )}
    </div>
  );
};

const ProgressBarDemo = () => {
  const [uploadProgress, setUploadProgress] = useState(45);
  const [processingProgress, setProcessingProgress] = useState(78);
  const [taskProgress, setTaskProgress] = useState(100);

  const simulateProgress = (setter, max = 100) => {
    setter(0);
    const interval = setInterval(() => {
      setter((prev) => {
        if (prev >= max) {
          clearInterval(interval);
          return max;
        }
        return prev + Math.random() * 15;
      });
    }, 200);
  };

  return (
    <div
      style={{
        padding: "2rem",
        backgroundColor: "#f8fafc",
        minHeight: "100vh",
      }}
    >
      <div style={{ maxWidth: "800px", margin: "0 auto" }}>
        <h1
          style={{
            textAlign: "center",
            marginBottom: "3rem",
            color: "#1e293b",
            fontSize: "2.5rem",
            fontWeight: "bold",
          }}
        >
          Advanced Progress Bar Component
        </h1>

        <div style={{ display: "flex", flexDirection: "column", gap: "3rem" }}>
          <div>
            <h2 style={{ marginBottom: "1rem", color: "#374151" }}>
              File Upload Progress
            </h2>
            <ProgressBar
              percentage={uploadProgress}
              type="upload"
              label="Uploading documents.zip"
              size="large"
            />
            <button
              onClick={() => simulateProgress(setUploadProgress)}
              style={{
                marginTop: "1rem",
                padding: "0.75rem 1.5rem",
                backgroundColor: "#3b82f6",
                color: "white",
                border: "none",
                borderRadius: "0.5rem",
                cursor: "pointer",
                fontSize: "1rem",
                fontWeight: "500",
              }}
            >
              Start Upload
            </button>
          </div>

          <div>
            <h2 style={{ marginBottom: "1rem", color: "#374151" }}>
              Data Processing
            </h2>
            <ProgressBar
              percentage={processingProgress}
              type="processing"
              label="Processing customer data"
              fillColor="#8b5cf6"
              size="medium"
            />
            <button
              onClick={() => simulateProgress(setProcessingProgress)}
              style={{
                marginTop: "1rem",
                padding: "0.75rem 1.5rem",
                backgroundColor: "#8b5cf6",
                color: "white",
                border: "none",
                borderRadius: "0.5rem",
                cursor: "pointer",
                fontSize: "1rem",
                fontWeight: "500",
              }}
            >
              Start Processing
            </button>
          </div>

          <div>
            <h2 style={{ marginBottom: "1rem", color: "#374151" }}>
              Task Completion
            </h2>
            <ProgressBar
              percentage={taskProgress}
              type="task"
              label="Completing project setup"
              fillColor="#f59e0b"
              size="small"
            />
            <button
              onClick={() => simulateProgress(setTaskProgress)}
              style={{
                marginTop: "1rem",
                padding: "0.75rem 1.5rem",
                backgroundColor: "#f59e0b",
                color: "white",
                border: "none",
                borderRadius: "0.5rem",
                cursor: "pointer",
                fontSize: "1rem",
                fontWeight: "500",
              }}
            >
              Start Task
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProgressBarDemo;
